import { SAVE_USER_INFO } from '../action_types'
const initState = {
    user:{},
    isLogin:false
}
export default function test(preState = initState, action) {
    const { type, data } = action
    let newState
    switch (type) {
        case SAVE_USER_INFO:
            newState = {user:data, isLogin:true}
            return newState
        default:
            return preState
    }
}
