import {SAVE_USER_INFO} from '../action_types'
export const createSaveUserInfoAction = (value) => {
    localStorage.setItem('user', JSON.stringify(value))
    localStorage.setItem('isLogin', true)
    return {type: SAVE_USER_INFO, data: value}
}
